function getStorageWithExpiration(key: string) {
  const itemStr = localStorage.getItem(key)

  // 检查是否存在对应的值
  if (!itemStr) {
    return null
  }
  let item = { expiry: 1, value: '' }
  try {
    item = JSON.parse(itemStr)
  } catch {
    item = { expiry: 1, value: '' }
