        throw errText.value
      }
    })
    .catch((ex: any) => {
      //程序发生异常
      console.error(ex)
      if (ex.code == 'ELD_UNSUPPORT') {
        ELD_UNSUPPORT.value = true
        errText.value = '当前浏览器无法打开摄像头,请换手机尝试'
        showInfoToast(errText.value)
        console.log(errText.value)
