@import '@/assets/styles/sprite-home.scss';
@import '@/assets/styles/sprite-001.scss';
.blue-bg {
  width: 100%;
  height: 30px;
  background-color: var(--van-button-primary-background);
}

.activity-bg {
  position: relative;
  width: 10rem;
  flex: 0 0 460px;
  background: url('@/assets/images/activity/activity-banner-bg.png') no-repeat left top / cover;
  background-size: 100% 422px;
  .nav-bar--icons {
    background-color: transparent;
  }
  .bulletin-container {
    width: 676px;
    height: 47px;
    margin: 0 auto;
    padding: 0 18px;
    color: #fff1cf;
    font-size: 23px;
    background-color: rgba(0, 0, 0, 0.12);
    border-radius: 30px;
  }

  .btn-activity-container {
    position: absolute;
    width: 286px;
    height: 85px;
    bottom: 0;
    left: 50%;
    transform: translate3d(-50%, 0, 0);
    overflow: hidden;
  }
  .btn-get-now {
    position: relative;
    width: 100%;
    height: 100%;
    background: url('@/assets/images/activity/btn-get-now.png') no-repeat center / cover;
    background-size: 100% 100%;
    animation: btnScale 1s ease-in-out infinite;
    &::after {
      content: '';
      width: 100px;
      height: 85px;
      background: url('@/assets/images/activity/btn-guang.png') no-repeat center / cover;
      background-size: 100% 100%;
      position: absolute;
      top: 0;
      left: 0;
      opacity: 0;
      animation: glareMove 2.5s 1s linear infinite;
    }
  }

  .yanhua01 {
    width: 56px;
    height: 75px;
    position: absolute;
    top: 125px;
    left: 0;
    opacity: 0;
    animation: yanhuaBloom01 3s ease-in-out infinite;
  }
  .yanhua02 {
    width: 53px;
    height: 53px;
    position: absolute;
    top: 143px;
    left: 124px;
    opacity: 0;
    animation: yanhuaBloom01 3s 1s ease-in-out infinite;
  }
  .yanhua03 {
    width: 89px;
    height: 67px;
    position: absolute;
    top: 128px;
    right: 0;
    opacity: 0;
    animation: yanhuaBloom01 3s 2s ease-in-out infinite;
  }
  .denglong {
    width: 77px;
    height: 84px;
    position: absolute;
    top: 113px;
    right: 124px;
    opacity: 0;
    animation: denglongMove 2s 2s ease-in-out infinite;
  }

  .border-highlight {
    width: 130px;
    height: 57px;
    background: url('@/assets/images/activity/border-guang.png') no-repeat center / cover;
    background-size: 100% 100%;
    position: absolute;
    bottom: 250px;
    // left: 186px;
    left: 170px;
    opacity: 0;
    animation: borderHighlight 4s ease-in-out infinite;
  }
}

.home-container {
  position: relative;
  width: 100%;
  height: calc(100% - 30px);
  margin-top: -30px;
  background-color: #ebf1f6;
  overflow: hidden;
  border-top-left-radius: 15px;
  border-top-right-radius: 15px;
  &.mt-t0 {
    margin-top: 0;
    .go-banzhuan {
      top: 30px;
    }
  }

  .topbar-container {
    width: 100%;
    height: 264px;
    flex: 0 0 264px;
    padding: 0 30px;
    box-sizing: border-box;
    background-color: var(--van-button-primary-background);

    .topbar-content-view {
      position: relative;
      width: 100%;
      height: 90px;

      .top-icon-bg {
        display: flex;
        align-items: center;
        justify-content: space-between;
        width: 132px;
        height: 50px;
        padding: 0 19px;
        background: v-bind('topIconBg') no-repeat center / cover;
      }

      .logo-container {
        position: absolute;
        top: 48px;
        left: 50%;
        transform: translate3d(-50%, 0, 0);
        display: flex;
        justify-content: space-between;
        align-items: center;

        .home-top-logo {
          width: 45px;
        }

        .text-logo {
          margin-left: 10px;
          font-size: 42px;
          color: #fff;
        }
      }

      .help {
        margin: 0 28px;
      }

      .message {
        position: relative;
        margin-bottom: -3px;
        margin-right: auto;
      }

      .unread-sign {
        position: absolute;
        top: -7px;
        right: -9px;
        width: 24px;
        height: 24px;
        line-height: 24px;
        background-color: red;
        color: #fff;
        font-size: 14px;
        text-align: center;
        border-radius: 50%;
      }
    }
  }

  .main-content-container {
    flex: 1;
    width: 100%;
    padding: 0 20px;

    .wallet-info-container {
      position: relative;
      width: 100%;
      z-index: 1;

      .wallet-info {
        width: 100%;
        // margin-top: -140px;
        border-radius: 14px;
        background-color: #fff;

        .wallet-main-content {
          padding: 21px;
          font-size: 26px;
          color: #868d9a;

          .text-funding-details {
            color: var(--van-button-primary-background);
          }
        }
      }
    }
  }

  .go-banzhuan {
    position: absolute;
    right: 0;
    top: 356px;
    width: 198px;
    height: 52px;
    padding: 10px 0 12px 22px;
    color: #fff;
    background-color: #fea703;
    font-size: 25px;
    border-top-left-radius: 26px;
    border-bottom-left-radius: 26px;
    white-space: nowrap;
    img {
      width: 31px;
      height: 31px;
      margin-right: 7px;
    }
    .process-order-sign {
      position: absolute;
      top: -7px;
      right: 6px;
      width: 35px;
      height: 35px;
      line-height: 35px;
      background-color: #d9001c;
      color: #fff;
      font-size: 21px;
      text-align: center;
      border-radius: 50%;
    }
  }

  :deep(.dialog-open-notice) {
    width: 708px;
    height: auto;
    padding: 40px;

    .van-dialog__header {
      padding: 0;
      font-size: 29px;
      color: #13161b;
    }

    .van-dialog__content {
      max-height: 600px;
      margin: 33px 0 35px 0;
      font-size: 25px;
      color: #787f8c;
      text-align: justify;
      overflow-y: auto;
    }

    .dialog-content {
      img {
        width: 100%;
        &[alt*='['] {
          width: auto;
        }
      }
    }

    .van-dialog__footer {
      padding: 0;
    }

    .van-dialog__confirm,
    .btn-confirm {
      width: 100%;
      height: 80px;
      font-size: 31px;
      background: var(--van-button-primary-background);
      color: #fff;
      border: 0;
      border-radius: 8px;
    }

    .dialog-radio-controler {
      margin-top: 20px;

      .radio {
        margin-right: 10px;
      }

      .radio-uncheck {
        width: 35px;
        height: 35px;
        border: 1px solid #868d9a;
        border-radius: 50%;
      }

      .radio-text {
        font-size: 25px;
        color: #13161b;
      }
    }

    .icon-close {
      position: absolute;
      right: 40px;
      top: 24px;
    }

    img {
      width: 100%;
      margin-top: 20px;
    }

    &.dialog-bg-img {
      padding: 0;

      .van-dialog__content {
        height: 572px;
        max-height: 600px;
        margin: 0;
        background: v-bind('dialogBgImg') no-repeat center / cover;
        overflow-y: auto;
      }

      .van-dialog__confirm {
        margin: 0 40px 40px 40px;
      }

      .btn-confirm {
        width: 626px;
        margin: 0 auto;
        margin-top: 30px;
      }

      .dialog-radio-controler {
        margin: 20px auto;
      }
    }
  }
}

@keyframes btnScale {
  0% {
    transform: scale(0.85);
  }
  50% {
    transform: scale(1);
  }
  100% {
    transform: scale(0.85);
  }
}
@keyframes glareMove {
  0% {
    left: -20px;
    opacity: 0;
  }
  5% {
    opacity: 0.5;
  }
  45% {
    opacity: 0.5;
  }
  55% {
    left: 170px;
    opacity: 0;
  }
  100% {
    left: 170px;
    opacity: 0;
  }
}
@keyframes yanhuaBloom01 {
  0% {
    transform: scale(0);
    opacity: 0;
  }
  25% {
    transform: scale(1);
    opacity: 0.8;
  }
  30% {
    opacity: 0;
  }
  100% {
    opacity: 0;
  }
}
@keyframes denglongMove {
  0% {
    transform: translate3d(0, 0, 0);
    opacity: 0.7;
  }
  40% {
    transform: perspective(500px) translate3d(90px, -120px, -500px);
    opacity: 0;
  }
  100% {
    opacity: 0;
  }
}
@keyframes borderHighlight {
  0% {
    opacity: 0;
  }
  5% {
    opacity: 1;
  }
  30% {
    opacity: 1;
    left: 440px;
  }
  35% {
    opacity: 0;
    left: 440px;
  }
  100% {
    opacity: 0;
  }
}

