@keyframes btnScale {
  0% {
    transform: scale(0.85);
  }
  50% {
    transform: scale(1);
  }
  100% {
    transform: scale(0.85);
  }
}

@keyframes glareMove {
  0% {
    left: -0.26667rem;
    opacity: 0;
  }
  5% {
    opacity: 0.5;
  }
  45% {
    opacity: 0.5;
  }
  55% {
    left: 2.26667rem;
    opacity: 0;
  }
  100% {
    left: 2.26667rem;
    opacity: 0;
  }
}

@keyframes yanhuaBloom01 {
  0% {
    transform: scale(0);
    opacity: 0;
  }
  25% {
    transform: scale(1);
    opacity: 0.8;
  }
  30% {
    opacity: 0;
  }
  100% {
    opacity: 0;
  }
}

@keyframes denglongMove {
  0% {
    transform: translate3d(0, 0, 0);
    opacity: 0.7;
  }
  40% {
    transform: perspective(6.66667rem) translate3d(1.2rem, -1.6rem, -6.66667rem);
    opacity: 0;
  }
  100% {
    opacity: 0;
  }
}

@keyframes borderHighlight {
  0% {
    opacity: 0;
  }
  5% {
    opacity: 1;
  }
  30% {
    opacity: 1;
    left: 5.86667rem;
  }
  35% {
    opacity: 0;
    left: 5.86667rem;
  }
  100% {
    opacity: 0;
  }
}

