@import '@/assets/styles/sprite-point.scss';

.today-activity-container {
  width: 100%;
  padding: 30px 20px;
  margin-bottom: 20px;
  background-color: #fff;
  border-radius: 14px;
  .container-title {
    font-size: 28px;
    text-align: left;
    margin-bottom: 15px;
    color: #13161b;
    span {
      color: #ffc000;
    }
  }
  .progressbar-container {
    position: relative;
    margin-bottom: 15px;
    .progressbar-grey,
    .progressbar-yellow {
      position: absolute;
      left: 0;
      top: 44px;
      height: 20px;
    }
    .progressbar-grey {
      width: calc(100% - 20px);
    }
    .progressbar-yellow {
      width: 0;
      left: -1px;
      border-top-left-radius: 20px;
      border-bottom-left-radius: 20px;
    }
    .award-point-wrapper {
      // position: relative;
      height: 114px;
      .award-box {
        width: 106px;
        position: relative;
        z-index: 9;
      }
      .award-gray-box {
        filter: grayscale(100%);
      }
      .award-point-value {
        margin-top: -16px;
        font-size: 21px;
        color: #ffc000;
      }
    }
    .point-80 {
      margin-top: -4px;
    }
    .item-point-column {
      position: absolute;
      left: 0;
      transition: all 0.3s ease-in-out;
      [class*='point-'] {
        position: relative;
        z-index: 9;
      }
      .light-step {
        position: absolute;
        top: -6px;
        animation: lightRotate 4.3s linear infinite;
      }
      .received {
        position: absolute;
        top: calc(50% - 8px);
        transform: translate3d(0, -50%, 0);
        // left: 6px;
        z-index: 10;
      }
    }
  }
  .task-container {
    .task-tips {
      font-size: 25px;
      color: #a1a7b2;
      text-align: left;
    }
  }

  .van-toast {
    width: 270px;
    height: 270px;
    padding: 40px;
    font-size: 33px;
    .van-toast__icon {
      font-size: 110px;
    }
    .van-toast__text {
      margin-top: 15px;
      line-height: 1.5;
    }
  }
}

@keyframes lightRotate {
  to {
    transform: rotate(360deg);
  }
}

