    el.addEventListener('click', function () {
      clicks++
      // console.log('clicks3', clicks)
      timeout = setTimeout(() => {
        clicks = 0
      }, 700) // 设置延时
      if (clicks === 3) {
        clearTimeout(timeout)
        clicks = 0
        binding.value()
      }
    })
  }
}

