const generateQR = (text: string) => {
  return new Promise(async (resolve, reject) => {
    try {
      const base64Url: string = await QRCode.toDataURL(text, { width: 225 })
      // console.log('base64Url', base64Url)
      resolve(base64Url)
    } catch (err) {
      console.error(err)
      reject(false)
    }
  })
}
