@import '@/assets/styles/sprite-home.scss';

:deep(.van-nav-bar) {
  .van-nav-bar__right {
    top: 95px;
    height: 50px;
    padding-right: 40px;
  }
  .btn-text {
    font-size: 33px;
    color: var(--van-button-primary-background);
  }
}

.message-container {
  width: 100%;
  height: calc(100% - 100px);
  padding: 50px 0 20px 0;
  background-color: #fff;
  &.with-controller {
    padding-bottom: 174px;
  }
  :deep(.van-pull-refresh) {
    overflow: auto;
  }
  .item-radio {
    flex: 0 0 29px;
    height: 29px;
    margin: 23px 20px 0 0;
    border-radius: 15px;
    border: solid 3px #868d9a;
  }
  .radio-checked {
    $raido-scale: calc(29 / 35);
    flex: 0 0 29px;
    --sprite-home-background-size: calc(108px * 7 / 10 * #{$raido-scale})
      calc(730px * 7 / 10 * #{$raido-scale});
    // height: 29px;
    // margin: calc(29 / 35 * 23px) calc(29 / 35 * 20px) 0 0;
    background: url($spriteHome) -0 calc(-282px * $sprite-computed-factor * $raido-scale) no-repeat;
    width: calc(50px * $sprite-computed-factor * $raido-scale);
    height: calc(50px * $sprite-computed-factor * $raido-scale);
    background-size: var(--sprite-home-background-size);
    margin: 23px 20px 0 0;
    // transform: scale(calc(29 / 35));
  }
  .message {
    background: url($spriteHome) 0px calc(-120px * $sprite-computed-factor) no-repeat,
      linear-gradient(to bottom, #999, orange);
    background-blend-mode: soft-light;
    background-size: var(--sprite-home-background-size);
    margin: 20px 13px 0 0;
    flex: 0 0 calc(54px * $sprite-computed-factor);
  }
  .icon-message {
    width: 38px;
    height: 38px;
    margin: 20px 13px 0 0;
  }
  .message-item {
    margin-bottom: 40px;
    padding: 0 32px;
    &.message-item-end {
      margin-bottom: 0;
    }
    .unread {
      flex: 0 0 14px;
      width: 14px;
      height: 14px;
      margin-right: 3px;
      background-color: #ff0020;
      border-radius: 50%;
    }
  }
  .item-title {
    font-size: 28px;
  }
  .item-cdesc {
    font-size: 22px;
    color: #787f8c;
  }
  .item-add_time {
    margin-top: 14px;
    font-size: 22px;
    color: #787f8c;
  }

  .no-data {
    position: absolute;
    left: 50%;
    top: 35%;
    transform: translate3d(-50%, -50%, 0);
    img {
      width: 240px;
    }
    .text-no-data {
      width: 100%;
      margin-bottom: 22px;
      font-size: 18px;
    }
  }

  .data-loading {
    position: absolute;
    top: 35%;
    left: 50%;
    transform: translate3d(-50%, -50%, 0);
  }

  :deep(.van-swipe-cell__wrapper) {
    .van-button {
      width: 180px;
      height: 100%;
    }
    .van-button__icon {
      font-size: 46px;
    }
  }
}

.btn-controller-container {
  position: fixed;
  bottom: -100%;
  width: 100%;
  max-width: 10rem;
  height: 174px;
  background-color: #fff;
  transition: bottom 0.5s ease-in;
  will-change: auto;
  &.show-controller {
    bottom: 0;
  }
  &.hairline-border::after {
    border-width: 0 !important;
    // border-top-width: 1px !important;
  }

  :deep(.van-button) {
    width: 205px;
    height: 80px;
    font-size: 31px;
    border-radius: 7px;
    &.btn-all-select {
      margin: 0 17px 0 17px;
    }
    &.van-button--disabled {
      opacity: 1;
      background-color: #828282;
      border-color: #828282;
    }
  }
}

.confirm-sheet-container {
  :deep(.van-action-sheet) {
    .van-action-sheet__description {
      height: 92px;
      line-height: 92px;
      padding: 0;
      font-size: 25px;
      color: #787f8c;
      &::after {
        height: 1px;
        background-image: linear-gradient(#e1e1e1, #e1e1e1), linear-gradient(#d6dce8, #d6dce8);
        border: 0;
        transform: scaleY(1);
        left: 0;
        right: 0;
      }
    }
    .van-action-sheet__item {
      height: 92px;
      font-size: 31px;
      font-weight: bold;
      color: var(--van-button-primary-background);
    }
    .van-action-sheet__gap {
      height: 1px;
      background: unset;
      background-image: linear-gradient(#e1e1e1, #e1e1e1), linear-gradient(#d6dce8, #d6dce8);
    }
    .van-action-sheet__cancel {
      height: 138px;
      font-size: 31px;
      font-weight: bold;
      color: #13161b;
    }
  }
}

