@import '@/assets/styles/sprite-point.scss';

.lucky-draw-container {
  position: relative;
  width: 100%;
  height: 460px;
  margin: 30px 0;
  background: url('@/assets/images/common/lucky-draw.png') center / cover no-repeat;
  .draw-middle-container {
    position: absolute;
    top: 97px;
    left: 15px;
    width: 680px;
    height: 258px;
    padding: 15px 22px;
    overflow: auto;
    .award-loop-container {
      width: 472px;
      min-width: 472px;
      gap: 13px;
      .item-award {
        width: 84px;
        height: 105px;
        justify-content: space-between;
        padding-bottom: 9px;
        background-color: #fff;
        border-radius: 7px;
        &.active {
          border: 6px solid #57fb07;
        }
        .icon-container {
          height: calc(100% - 28px);
          transform: scale(0.85);
          .RMB-gold-box,
          .RMB-silver-box,
          .lucky-star,
          .point-8 {
            margin-top: 5px;
          }
          img {
            width: 100%;
          }
        }
        .award-name {
          font-size: 17px;
          color: #cf0224;
        }
        .final-award-name {
          font-size: 16px;
        }
      }
    }
    .right-scroll-container {
      align-self: flex-start;
      margin-left: 10px;
      color: #fff;
      .title {
        margin-bottom: 10px;
        font-size: 24px;
        color: #ffdae0;
        text-align: left;
      }
      .winning-record {
        // display: block;
        width: 146px;
        height: 180px;
        gap: 20px;
        font-size: 18px;
        color: #fabe73;
        overflow: auto;
        .seamless-scroll {
          height: 180px;
          overflow: hidden;
        }
        .item-award-history {
          width: 100%;
          flex: 0 0 26px;
        }
        .no-data {
          img {
            width: 140px;
            // height: 110px;
          }
          .text-no-data {
            width: 100%;
            margin-bottom: 22px;
            font-size: 12px;
          }
        }
      }
    }
  }
  .lottery-container {
    position: absolute;
    left: 15px;
    bottom: 15px;
    width: calc(100% - 30px);
    height: 80px;
    .award-btn {
      position: relative;
      text-align: center;
      .discount {
        position: absolute;
        top: -18px;
        right: -14px;
        .discount-num {
          position: absolute;
          top: 26px;
          left: 50%;
          transform: translate3d(-50%, -50%, 0);
          font-size: 17px;
          color: #fff;
          white-space: nowrap;
        }
      }
    }
    .item-name {
      font-size: 25px;
      color: #cf0224;
      font-weight: bold;
    }
    .item-required-point {
      font-size: 17px;
      color: #bb7100;
    }
  }

  .award-result-dialog {
    .content {
      padding-top: 17px !important;
      padding-bottom: 17px !important;
      .result-summary {
        padding-bottom: 17px;
        font-size: 25px;
        color: #787f8c;
        border-bottom: 1px solid #d9dde8;
      }
      .result-value {
        margin-left: 16px;
        color: #13161b;
      }
      .award-list-container {
        width: 100%;
        height: 270px;
        padding: 18px 10px 0 10px;
        gap: 15px 0;
        overflow-y: auto;
        .item-award {
          width: 105px;
          height: 105px;
          padding: 5px 0;
          color: #8e8f90;
          background-color: #fff;
          border-radius: 7px;
          .award-icon {
            width: 60%;
            img {
              margin-top: 0;
            }
          }
          .award-name {
            font-size: 22px;
          }
        }
      }
    }
  }
}

