        justify-content: flex-start;
        .digital_wrap {
          width: auto;
        }
        .digital {
          background-image: linear-gradient(#fdf4a7, #fdd73e);
          background-image: -webkit-linear-gradient(#fdf4a7, #fdd73e);
          color: transparent;
          background-clip: text;
          -webkit-background-clip: text;
        }
        .icon {
          height: auto;
          background-image: linear-gradient(#fdf4a7, #fdd73e);
          background-image: -webkit-linear-gradient(#fdf4a7, #fdd73e);
          color: transparent;
          background-clip: text;
          -webkit-background-clip: text;
          display: inline-block;
        }
      }
    }
  }
  .loop-area {
    position: absolute;
    left: 50%;
    top: 190px;
    transform: translate3d(-50%, 0, 0);
    width: 443px;
    height: 352px;
    padding: 2px 4px;
    background: #742a11;
    border-radius: 20px;
    .item-card {
      width: 143px;
      height: 114px;
      background: url('@/assets/images/activity/card.png') no-repeat center / cover;
      background-size: 100% 100%;
      &.active {
        background-image: url('@/assets/images/activity/card-highlight.png');
      }
    }
    .card-pic {
      width: 70px;
      height: 100px;
    }
    .card-xxcy {
      width: 126.8px;
      height: 100px;
    }
  }
  .game-award-record-container {
    position: absolute;
    left: -10px;
    bottom: 284px;
    width: auto;
    height: 105px;
    overflow: hidden;
    .seamless-scroll {
      height: 100%;
      margin-top: 0;
      padding: 0 20px;
      // transition: all 0.5s ease-in-out;
      // overflow: hidden;
    }
    .item-award-history {
      width: 100%;
      height: 28px;
      // margin-top: 0;
      // margin-bottom: 10px;
      padding: 0 23px;
      color: #eeede8;
      background-color: rgba(0, 0, 0, 0.12);
      opacity: 0.5;
      // transform-origin: center top;
      transform: translate3d(0, 0, 0) scale(1);
      transition: all 0.5s ease-in-out;
      font-size: 18px;
      border-radius: 30px;
      &:nth-child(2) {
        opacity: 1;
        transform: translate3d(0, 10px, 0) scale(1.1);
      }
      &:nth-child(3) {
        transform: translate3d(0, 20px, 0) scale(1);
      }
      &:nth-child(4) {
        transform: translate3d(0, 30px, 0) scale(1);
      }
    }
    .award-mask-top,
    .award-mask-bottom {
      position: absolute;
      top: 0;
      left: 0;
      z-index: 1;
      width: 100%;
      height: 27px;
      background-image: linear-gradient(180deg, rgba(255, 255, 255, 0.5), rgba(255, 255, 255, 0.5)),
        linear-gradient(0deg, rgba(255, 255, 255, 0.5), rgba(255, 255, 255, 0.5));
      background-repeat: no-repeat;
      background-position: top, bottom;
      background-size: 100% 27px;
      transform: translatez(0);
      border-radius: 30px;
      pointer-events: none;
    }
    .award-mask-bottom {
      top: 74px;
    }
  }
  .lottery-btn-container {
    position: absolute;
    bottom: 180px;
    width: 100%;
    [class*='btn-'] {
      display: flex;
      justify-content: center;
      width: 283px;
      height: 88px;
      padding-top: 5px;
      font-size: 28px;
      font-weight: 700;
      color: #b95431;
      background: url('@/assets/images/activity/btn-lottery.png') no-repeat center / cover;
      background-size: 100% 100%;
    }
    .btn-lottery-2 {
      background-image: url('@/assets/images/activity/btn-lottery-2.png');
    }
    .btn-flip-card-now {
      margin-right: 54px;
    }
  }
  .countdown-container {
    width: 394px;
    position: absolute;
    bottom: 27px;
    left: 178px;
    font-size: 30px;
    color: #fff4c3;
    &.countdown-less-24 {
      left: 170px;
    }
    :deep(.van-count-down) {
      display: flex;
      width: auto;
      font-size: 30px;
      color: #fff4c3;
      white-space: nowrap;
      .time {
        width: 145px;
        &.time-less-24 {
          width: 194px;
          margin-left: -16px;
        }
        span {
          flex: 0 0 40px;
          text-align: center;
          &.colon {
            flex: 0 0 33px;
          }
          &.num {
            color: #eed753;
            font-weight: bold;
          }
        }
      }
    }
  }
}
.collected-card-container {
  position: relative;
  width: 100%;
  height: 522px;
  background: url('@/assets/images/activity/hollow-base.png') no-repeat center / cover;
  background-size: 100% 100%;
