    }
  }
  .with-card {
    color: #2d2d2d;
  }
  .no-card {
    color: #a0a0a0;
    img {
      filter: grayscale(100%);
    }
  }
  .red-dot {
    position: absolute;
    right: -10px;
    top: -10px;
    width: 48px;
    height: 48px;
    padding: 2px;
    color: #fff;
    border-radius: 50%;
    background-image: linear-gradient(0deg, #f0a564 27%, #fbe8bc 100%);
    box-shadow: 0px 1px 2px 0px rgba(138, 41, 35, 0.7);
    &::after {
      content: attr(data-count);
      width: 100%;
      height: 100%;
      border-radius: 50%;
      background-image: linear-gradient(#dd4e53, #dd4e53);
      display: flex;
      justify-content: center;
      align-items: center;
      font-size: 29px;
      color: #f7d6c2;
    }
  }

