    console.log(payment, 'payment')
    if (payment.status === -1 || payment.status === 2) return
    if (
      payment_list.value.length >= bandcard_num?.value?.[payment_way_to_type?.[route?.query?.way]]
    )
      return
    router.push('/rmb')
  },
  USDT_TRC20: (payment: any) => {
    if (payment.status === -1 || payment.status === 2) return
    if (
      payment_list.value.length >= bandcard_num?.value?.[payment_way_to_type?.[route?.query?.way]]
