const set_default_payment = async (payment: any) => {
  try {
    const res = await setDefaultWithdrawAddressApi({
      id: payment.id
    })
    if (!res.code) {
      showSuceedToast('设置默认成功')
      updateList()
    }
  } catch (e) {
    console.log(e)
  }
}

