  _homeNoticeShowList[currArticleIndex.value] = dialog.allowShow ? true : dateNow
  updateHomeNoticeList(_homeNoticeShowList)

  currArticleIndex.value++
  dialog.show = false

  setTimeout(() => {
    window.log(
      '关闭',
      currArticleIndex.value,
      dialog,
      _homeNoticeShowList,
      homeNoticeShowList.value[currArticleIndex.value]
    )

