        if (+from <= +num.value && +num.value <= +to) {
          amountFrom = +num.value * (1 - vRate / 100);
          amountTo = +num.value * (1 + vRate / 100);
          break;
        }
      }
      if (!amountFrom && !amountTo)
        return num.value;
      return `${amountFrom}-${amountTo}`;
    });
