          return showToast({
            message: `推荐购买 ${min}~${max} 或 全部`,
            icon: info
          });
        }
      }
      if (num2 > +props.sellData.remaining_num)
        return showToast({
          message: "剩余可购买金额" + (+props.sellData.remaining_num).toTruncFixed(3),
          icon: info
