        const { order_id } = res;
        if (!order_id) {
          if (res.code === 10208) {
            await getBuySellCoinInfo({});
            const msgtext = `
          </br>
          </br>
          你还有进行中的订单 <span class="red_f">【${buy_orders.value[0].buy_num}元】</span> ，请完成支付或者取消重新下单。</br>
          </br>
          </br>
