              message: msgtext,
              allowHtml: true,
              messageAlign: "center",
              className: "selldialog",
              confirmButtonText: "前往支付",
              cancelButtonText: "取消上笔订单",
              action: () => {
                router.push(`/${targetRoute}?order_id=${buy_orders.value[0].buy_order_id}`);
              },
              cancelAction: () => {
                BuyCancel({ buy_order_id: buy_orders.value[0].buy_order_id }).then(() => {
                  showToast({
                    message: "操作成功",
                    icon: info
                  });
                });
              }
            });
            return;
          }
          if (res.code === 10229) {
            const msgtext = `
          </br>
          </br>
          订单失效，请返回重新下单!
          </br>
          </br>
          `;
