          sessionStorage.setItem("pay_cardid", "");
          sessionStorage.setItem("pay_bankname", "");
          if (route.fullPath.includes("index")) {
            router.push(`/${targetRoute}?order_id=${order_id}`);
          } else {
            router.replace(`/${targetRoute}?order_id=${order_id}`);
          }
        });
      });
