const tabChange = (code: string) => {
  // console.log('code', code)
  if (code == '翻卡记录') {
    // dataList.value = sendList.value
    parm.value.type = 1
  } else if (code == '奖励记录') {
    // dataList.value = sendList.value
    parm.value.type = 2
  } else {
    parm.value.type = 3
    // dataList.value = receiveList.value
  }
  refreshing.value = true
  loading.value = true
  onRefresh()
}

const changeMenu = (e: number) => {
  // console.log('e', e)
  parm.value.week = e
  refreshing.value = true
  loading.value = true
  onRefresh()
}

