  // console.log('key', key)

  const newData: Record<string, any> = {
    lang,
    platform,
    timestamp,
    key,
    ver
  }
  const sign = objToUrl(newData, true)

  // window.log('签名前字符串', sign, config.url)

  newData.sign = md5(sign).toString()
  delete newData.key

  if (type === 'get') return objToUrl(Object.assign(newData, config.params))

