 * -------------------- 调试用 --------------------
 */
export const MyLog = () => {
  const LOG: ILogType[] = ['log', 'info', 'warn', 'error']

  type ILogType = 'log' | 'info' | 'warn' | 'error'
  LOG.forEach((logType: ILogType) => {
    window[logType] = function (...args) {
      const [key, ...logInfo] = args
      let color

      switch (logType) {
        case 'log':
          color = 'hotpink'
          break
        case 'info':
          color = 'orange'
          break
        case 'warn':
          color = 'red'
          break
        case 'error':
          color = ''
          break
        default:
          color = ''
          break
      }

      console[logType](`%c${key}::`, `color:${color}`, ...logInfo)
    }
  })
}

