        font-family: MicrosoftYaHei;
        font-size: 25px;
        font-weight: normal;
        font-stretch: normal;
        line-height: 35px;
        letter-spacing: 0px;
        color: #787f8c;
      }
    }
    .process.processActive {
      .Onode {
        border: solid 3px var(--van-button-primary-background);
        .Pbg {
          background-color: var(--van-button-primary-background);
        }
      }
