  applyMerchantIndex.value = index
  const muuid = applyMerchantList.value[applyMerchantIndex.value]?.muuid ?? '123'
  gameReqApi({ muuid }).then(res => {
    if (!Array.isArray(res) && res.hasOwnProperty('errmsg')) {
      return
