    oderParm.value.page = 1
    list.value = []
    refreshing.value = false
    getOrderList()
  }

  // 切换状态后，列表滚动至顶部
  nextTick(() => {
    pullRefresh.value?.$el?.scrollTo(0, 0)
  })
})

const recordPageStatus = () => {
  if (onlineCharge.value) {
    router.replace({
