)
// 当前状态是否显示红点
const isShowBDot = computed(() => {
  return (index: number) => {
    const _list = list.value.filter(item => {
      if (action_index.value === 0) {
        return item.status === 3
      }
      if (index === 2) return item.status === 2
      if (index === 4) return item.status === 4
    })
    return !!_list.length
  }
