// 获取时间段范围
const getRange = (days: number = 30) => {
  const date_start = dayjs().subtract(days, 'day').format('YYYY-MM-DD')
  return {
    date_start: date_start,
    date_end: days == 1 ? date_start : dayjs().format('YYYY-MM-DD')
  }
}

