  if (onlineCharge.value) {
    router.replace({
      query: {
        active: 2,
        status_index: status_index.value
      }
    })
  } else {
    router.replace({
      query: {
        active_index: action_index.value,
        status_index: status_index.value
      }
    })
  }
}

