  show: false,
  title: '',
  img: '',
  content: '',
  confirmButtonText: ''
})

const canBindPayment = () => {
  if (authStore.userInfo.is_set_paypassword === 2) {
    const dialogInfo = {
      show: true,
      title: '设置支付密码',
      img: passIcon,
      content: '请先设置支付密码',
      confirmButtonText: '立即设置'
    }
    Object.assign(dialogContent, dialogInfo)
    return false
  }

  return true
}

